import React from 'react';
import {formatPrice} from '../helpers';

class Order extends React.Component{

renderOrder=(key)=>{
    const fish = this.props.fishes[key];
    const count = this.props.order[key];
    return <li>{count} lbs {fish.name}
    {formatPrice(count*fish.price)}
    </li>
}
//above, we separated out the rendering into a function that can be called below -- cleaner code!

    render() {

        const orderIds = Object.keys(this.props.order);//gets the key for each fish order.
        const total = orderIds.reduce((prevTotal, key)=>{
            const fish = this.props.fishes[key];
            const count = this.props.order[key];
            const isAvailable = fish && fish.status ==="available";

            if(isAvailable){
                return prevTotal+(count*fish.price);
            }
                return prevTotal;
            
        }, 0);
       
        return (

            <div className ="order-wrap">
               <h2>Order</h2> 
               <ul>
                   {orderIds.map(this.renderOrder)} 
        {/**Using map to map over each order, using key as argument, then displaying key */}
               </ul>
              <div className='total'>
              Total:
                  <strong>{formatPrice(total)}</strong>
              </div>
            </div>
        );
    }
}
export default Order;
